import React, { useRef, useState, useEffect, useCallback } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, GoogleAuthProvider, signInWithPopup, signInWithRedirect, getRedirectResult, signOut } from 'firebase/auth';

// Import the functions you need from the SDKs you need

import { getAnalytics } from "firebase/analytics";
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

// Your web app's Firebase configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyATZACrgNqKqIJM_KJbasCZMSm11n7JggM",
  authDomain: "wyshdrop.firebaseapp.com",
  projectId: "wyshdrop",
  storageBucket: "wyshdrop.firebasestorage.app",
  messagingSenderId: "203381869133",
  appId: "1:203381869133:web:f0550c309b74188923aa04",
  measurementId: "G-QX3PFXV4PX"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);

if (firebaseConfig.apiKey === "") {
  console.error(
    "************************************************************************************************************************\n" +
    "FIREBASE CONFIGURATION ERROR: Your Firebase API Key (or other config values) appears to be empty/placeholder.\n" +
    "Please replace the placeholder values in `firebaseConfig` with your ACTUAL project credentials from the Firebase Console.\n" +
    "Go to Firebase Console -> Project settings -> Your apps -> Web app to find these details.\n" +
    "Without correct credentials, Firebase authentication will NOT work (e.g., 'auth/api-key-not-valid' errors).\n" +
    "************************************************************************************************************************"
  );
}
const playSound = (playerRef, soundType, soundSettings) => {
  const player = playerRef.current;
  if (typeof window.Tone === 'undefined' || !player) {
    console.warn(`Cannot play sound. Tone.js is not defined or player is null for ${soundType}.`);
    return;
  }

  const { isAllSoundEnabled, isClickSoundEnabled, isScrollSoundEnabled, isPageTransitionSoundEnabled } = soundSettings;

  if (!isAllSoundEnabled) {
    return; // Do not play any sound if master toggle is off
  }

  // Check individual sound type settings
  if (soundType === 'click' && !isClickSoundEnabled) return;
  if (soundType === 'scroll' && !isScrollSoundEnabled) return;
  if (soundType === 'pageTransition' && !isPageTransitionSoundEnabled) return;

  if (player.loaded) {
    player.start();
  } else {
    console.warn(`Sound not loaded yet for ${soundType}:`, player.url);
  }
};

// Debounce function for scroll sound
const debounce = (func, delay) => {
  let timeout;
  return function(...args) {
    const context = this;
    clearTimeout(timeout);
    timeout = setTimeout(() => func.apply(context, args), delay);
  };
};

// Reusable ProductSection Component (used on Home page and Category pages)
const ProductSection = ({ title, onSeeMoreClick, onProductClick, onAddProductToWishlist, soundSettings, clickSoundRef }) => {
  const scrollRef = useRef(null);
  const [showLeftArrow, setShowLeftArrow] = useState(false);
  const [showRightArrow, setShowRightArrow] = useState(true);
  const scrollAmount = 300; // Pixels to scroll

  // Dummy product data for this section
  const dummyProducts = [...Array(10)].map((_, i) => ({ // Increased to 10 for better scrolling demo
    id: `${title.replace(/\s/g, '-')}-product-${i + 1}`,
    name: `${title} Item ${i + 1}`,
    image: `https://placehold.co/240x280/D3A173/FFFFFF?text=${title.replace(/\s/g, '+')}+Item+${i + 1}`, // Adjusted height for more room
    price: (20 + i * 5).toFixed(2),
    description: `A wonderful product from the ${title} collection. Perfect for gifting or keeping!`,
    rating: Math.floor(Math.random() * 2) + 4, // 4 or 5 stars
    comments: [
      { id: 1, user: "UserA", text: "Love this product! Highly recommend." },
      { id: 2, user: "UserB", text: "Great quality for the price." },
    ],
    amazonLink: `https://www.amazon.com/dp/B07PFV1QCH?tag=yourtag-${i}` // Placeholder Amazon link
  }));

  const checkScrollArrows = useCallback(() => {
    if (scrollRef.current) {
      const { scrollLeft, scrollWidth, clientWidth } = scrollRef.current;
      setShowLeftArrow(scrollLeft > 0);
      setShowRightArrow(scrollLeft + clientWidth < scrollWidth);
    }
  }, []);

  useEffect(() => {
    checkScrollArrows(); // Check on mount
    const currentRef = scrollRef.current;
    if (currentRef) {
      currentRef.addEventListener('scroll', checkScrollArrows);
      window.addEventListener('resize', checkScrollArrows); // Recheck on resize
    }
    return () => {
      if (currentRef) {
        currentRef.removeEventListener('scroll', checkScrollArrows);
      }
      window.removeEventListener('resize', checkScrollArrows);
    };
  }, [checkScrollArrows]);


  const handleScrollLeft = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({ left: -scrollAmount, behavior: 'smooth' });
      playSound(clickSoundRef, 'click', soundSettings); // Play click sound for scrolling
    }
  };

  const handleScrollRight = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({ left: scrollAmount, behavior: 'smooth' });
      playSound(clickSoundRef, 'click', soundSettings); // Play click sound for scrolling
    }
  };

  return (
    <section className="mb-12 relative"> {/* Increased margin-bottom for more space between sections */}
      <div className="flex justify-between items-center mb-4 ml-4 md:ml-12"> {/* Adjusted margin */}
        {/* Formal font for section titles */}
        <h2 className="text-3xl font-bold text-[var(--primary-color)]">{title}</h2>
        {/* See More link */}
        <a href="#" onClick={(e) => { e.preventDefault(); onSeeMoreClick(title); playSound(clickSoundRef, 'click', soundSettings); }} className="text-sm text-[var(--primary-color)] hover:underline mr-4 md:mr-12 flex items-center space-x-1 transition-all duration-200 ease-in-out hover:scale-105 active:scale-95"> {/* Added animation */}
          <span>See More</span>
        </a>
      </div>
      {/* Increased padding-bottom to pb-16 to ensure "Add to Wyshlist" button is fully visible */}
      <div ref={scrollRef} className="flex overflow-x-auto overflow-y-hidden pb-16 space-x-4 custom-scrollbar px-4 md:px-12">
        {/* Product Image Cards */}
        <div className="flex space-x-4"> {/* Added a wrapper div for consistent spacing */}
          <div className="flex space-x-4"> {/* Added a wrapper div for consistent spacing */}
            {dummyProducts.map((product) => (
              <div key={product.id} className="flex-shrink-0 w-56 h-80 bg-[var(--box-bg-color)] rounded-lg shadow-md border-2 border-[var(--border-color)] flex flex-col items-center justify-between p-2 cursor-pointer transition-all duration-200 ease-in-out hover:scale-105 active:scale-95">
                {/* Product image with adjusted height */}
                <div className="w-48 h-56 bg-[var(--main-bg-color)] rounded-md flex flex-col items-center justify-center text-[var(--primary-color)] text-lg relative mb-2"
                  onClick={() => { onProductClick(product); playSound(clickSoundRef, 'click', soundSettings); }}> {/* Adjusted padding and card size */}
                  <img src={product.image} alt={product.name} className="w-full h-full object-cover rounded-md" onError={(e) => e.target.src = `https://placehold.co/240x280/D3A173/FFFFFF?text=${product.name.replace(/\s/g, '+')}`} />
                  {/* Price inside the image box */}
                  <p className="absolute bottom-2 text-white bg-black bg-opacity-50 px-2 py-1 rounded-md text-sm">${product.price}</p>
                </div>
                <h3 className="text-lg font-semibold text-[var(--primary-color)] text-center mb-2">{product.name}</h3> {/* Reduced margin-bottom here */}
                <div className="flex flex-col space-y-3 w-full px-2"> {/* Increased space-y from 2 to 3 */}
                  <button
                    onClick={() => { onAddProductToWishlist(product); playSound(clickSoundRef, 'click', soundSettings); }}
                    className="px-4 py-2 bg-gray-500 text-white rounded-md shadow-md hover:bg-gray-600 transition-all duration-200 ease-in-out hover:scale-105 active:scale-95 w-full">
                    Add to Wyshlist
                  </button>
                  <a
                    href={product.amazonLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    onClick={() => playSound(clickSoundRef, 'click', soundSettings)}
                    className="px-4 py-2 bg-[var(--button-bg-color)] text-white rounded-md shadow-md hover:bg-opacity-90 transition-all duration-200 ease-in-out hover:scale-105 active:scale-95 text-center w-full"
                  >
                    Go Buy <i className="fas fa-external-link-alt ml-1"></i>
                  </a>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      {showLeftArrow && (
        <button
          onClick={handleScrollLeft}
          className="absolute left-4 md:left-12 top-1/2 -translate-y-1/2 bg-[var(--box-bg-color)] rounded-full p-2 shadow-md hover:bg-gray-100 focus:outline-none transition-all duration-200 ease-in-out hover:scale-105 active:scale-95"
          aria-label={`Scroll left for ${title} section`}
        >
          <i className="fas fa-arrow-left text-3xl text-[var(--primary-color)]"></i>
        </button>
      )}
      {showRightArrow && (
        <button
          onClick={handleScrollRight}
          className="absolute right-4 md:right-12 top-1/2 -translate-y-1/2 bg-[var(--box-bg-color)] rounded-full p-2 shadow-md hover:bg-gray-100 focus:outline-none transition-all duration-200 ease-in-out hover:scale-105 active:scale-95"
          aria-label={`Scroll right for ${title} section`}
        >
          <i className="fas fa-arrow-right text-3xl text-[var(--primary-color)]"></i>
        </button>
      )}
    </section>
  );
};

// Reusable Header Component
const Header = ({ toggleSidebar, onSearchClick, onBookmarkClick, onSettingsClick, onProfileClick, onBack, currentPage, onWyshDropClick, isHeaderVisible, toggleHeaderVisibility, user, onQuizMeClick, onDonateClick, onFeedbackClick, onSignInClick, onSignUpClick, soundSettings, clickSoundRef }) => {
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);

  return (
    <header className={`w-full bg-[var(--box-bg-color)] p-4 flex justify-between items-center shadow-sm rounded-b-xl transition-transform duration-300 ${isHeaderVisible ? 'translate-y-0' : '-translate-y-full'} fixed top-0 left-0 z-30`}>
      <div className="flex items-center space-x-4">
        {/* Back Button (conditionally rendered for search/bookmarks/settings/profile/category pages) */}
        {(currentPage !== 'home' && currentPage !== 'cover' && currentPage !== 'create-account') && (
          <button
            onClick={() => { onBack(); playSound(clickSoundRef, 'click', soundSettings); }}
            aria-label="Go back"
            className="text-[var(--primary-color)] hover:text-gray-700 focus:outline-none transition-all duration-200 ease-in-out hover:scale-105 active:scale-95"
          >
            <i className="fas fa-arrow-left text-xl"></i>
          </button>
        )}
        {/* Menu Button (only on home page or when not on other specific pages) */}
        {currentPage === 'home' && (
          <button
            onClick={() => { toggleSidebar(); playSound(clickSoundRef, 'click', soundSettings); }}
            aria-label="Open menu"
            className="text-[var(--primary-color)] hover:text-gray-700 focus:outline-none transition-all duration-200 ease-in-out hover:scale-105 active:scale-95"
          >
            <i className="fas fa-bars text-xl"></i>
          </button>
        )}
        {/* WyshDrop Logo/Text - Now links to home */}
        <div className="flex items-center space-x-2 cursor-pointer transition-all duration-200 ease-in-out hover:scale-105 active:scale-95" onClick={() => { onWyshDropClick(); playSound(clickSoundRef, 'click', soundSettings); }}>
          {/* Use an <img> tag for the logo. Ensure this is a transparent PNG for best results. */}
          <img src="https://placehold.co/40x40/transparent/9A6E45?text=Logo" alt="WyshDrop Logo" className="h-10 w-10 object-contain" />
          <h1 className="text-3xl font-handwritten text-[var(--primary-color)] flex items-center space-x-1">
            <span>WyshDrop</span>
          </h1>
        </div>
      </div>
      <nav className="flex space-x-6">
        {/* Search Icon */}
        <button aria-label="Search" className="text-[var(--primary-color)] hover:text-gray-700 focus:outline-none transition-all duration-200 ease-in-out hover:scale-105 active:scale-95" onClick={() => { onSearchClick(); playSound(clickSoundRef, 'click', soundSettings); }}>
          <i className="fas fa-search text-xl"></i>
        </button>
        {/* Saved Items Icon */}
        <button aria-label="Saved Items" className="text-[var(--primary-color)] hover:text-gray-700 focus:outline-none transition-all duration-200 ease-in-out hover:scale-105 active:scale-95" onClick={() => { onBookmarkClick(); playSound(clickSoundRef, 'click', soundSettings); }}>
          <i className="fas fa-bookmark text-xl"></i>
        </button>
        {/* Settings Icon */}
        <button aria-label="Settings" className="text-[var(--primary-color)] hover:text-gray-700 focus:outline-none transition-all duration-200 ease-in-out hover:scale-105 active:scale-95" onClick={() => { onSettingsClick(); playSound(clickSoundRef, 'click', soundSettings); }}>
          <i className="fas fa-cog text-xl"></i>
        </button>
        {/* User Profile Icon with Dropdown */}
        <div className="relative" onMouseEnter={() => setShowProfileDropdown(true)} onMouseLeave={() => setShowProfileDropdown(false)}>
          <button aria-label="User Profile" className="text-[var(--primary-color)] hover:text-gray-700 focus:outline-none transition-all duration-200 ease-in-out hover:scale-105 active:scale-95" onClick={() => { onProfileClick(); playSound(clickSoundRef, 'click', soundSettings); }}>
            <i className="fas fa-user-circle text-xl"></i>
            <span className="ml-2 text-sm hidden md:inline">{user.username || 'Guest'}</span>
          </button>
          {showProfileDropdown && (
            <div className="absolute right-0 mt-2 w-48 bg-[var(--box-bg-color)] rounded-md shadow-lg py-1 z-10 border border-[var(--border-color)]">
              {user.isLoggedIn ? (
                <>
                  <div className="px-4 py-2 text-sm text-[var(--primary-color)] border-b border-[var(--border-color)]">
                    <p className="font-semibold">{user.username}</p>
                    <p className="text-xs text-gray-500">{user.email}</p>
                  </div>
                  <button onClick={() => { onQuizMeClick(); playSound(clickSoundRef, 'click', soundSettings); }} className="block w-full text-left px-4 py-2 text-sm text-[var(--primary-color)] hover:bg-gray-100 transition-all duration-200 ease-in-out hover:scale-105 active:scale-95">Quiz Me ✨</button>
                  <button onClick={() => { onDonateClick(); playSound(clickSoundRef, 'click', soundSettings); }} className="block w-full text-left px-4 py-2 text-sm text-[var(--primary-color)] hover:bg-gray-100 transition-all duration-200 ease-in-out hover:scale-105 active:scale-95">Donate</button>
                  <button onClick={() => { onFeedbackClick(); playSound(clickSoundRef, 'click', soundSettings); }} className="block w-full text-left px-4 py-2 text-sm text-[var(--primary-color)] hover:bg-gray-100 transition-all duration-200 ease-in-out hover:scale-105 active:scale-95">Feedback</button>
                </>
              ) : (
                <>
                  <div className="px-4 py-2 text-sm text-[var(--primary-color)] border-b border-[var(--border-color)]">
                    <p className="font-semibold">Guest User</p>
                    <p className="text-xs text-gray-500">Sign in for full features!</p>
                  </div>
                  <button onClick={() => { onSignInClick(); playSound(clickSoundRef, 'click', soundSettings); }} className="block w-full text-left px-4 py-2 text-sm text-[var(--primary-color)] hover:bg-gray-100 transition-all duration-200 ease-in-out hover:scale-105 active:scale-95">Sign In</button>
                  <button onClick={() => { onSignUpClick(); playSound(clickSoundRef, 'click', soundSettings); }} className="block w-full text-left px-4 py-2 text-sm text-[var(--primary-color)] hover:bg-gray-100 transition-all duration-200 ease-in-out hover:scale-105 active:scale-95">Sign Up</button>
                </>
              )}
            </div>
          )}
        </div>
        {/* Hide/Show Header Button */}
        {currentPage === 'home' && ( // Only show on home page for now
          <button
            onClick={() => { toggleHeaderVisibility(); playSound(clickSoundRef, 'click', soundSettings); }}
            aria-label={isHeaderVisible ? "Hide header" : "Show header"}
            className="text-[var(--primary-color)] hover:text-gray-700 focus:outline-none ml-4 transition-all duration-200 ease-in-out hover:scale-105 active:scale-95"
          >
            <i className={`fas ${isHeaderVisible ? 'fa-chevron-up' : 'fa-chevron-down'} text-xl`}></i>
          </button>
        )}
      </nav>
    </header>
  );
};

// Home Content Component
const HomeContent = ({ onSeeMoreClick, onDiscoverClick, isLoggedIn, onAboutUsClick, onProductClick, onAddProductToWishlist, soundSettings, clickSoundRef }) => { // Added onProductClick prop
  return (
    <>
      {/* Main Content Area */}
      {/* Adjusted padding top for fixed header and nav bar */}
      <main className="flex-grow w-full py-12 pt-[130px]">
        {/* Welcome Section (only for new users) */}
        {!isLoggedIn && (
          <section className="w-full bg-[var(--box-bg-color)] py-12 mb-8 rounded-lg shadow-lg border-2 border-[var(--border-color)] text-center px-4 md:px-12"> {/* Adjusted padding */}
            <h2 className="text-4xl font-bold text-[var(--primary-color)] mb-4">Your Gifting Journey Starts Here!</h2>
            <p className="text-lg text-[var(--primary-color)] max-w-2xl mx-auto mb-4">
              Discover and share the perfect gifts with ease. Create wishlists, get personalized recommendations, and make every occasion memorable.
            </p>
            <button
              onClick={() => { onAboutUsClick(); playSound(clickSoundRef, 'click', soundSettings); }}
              className="px-6 py-3 bg-[var(--button-bg-color)] text-white font-bold rounded-md shadow-md hover:bg-opacity-90 transition-all duration-200 ease-in-out hover:scale-105 active:scale-95"
            >
              Learn More About WyshDrop
            </button>
          </section>
        )}


        {/* Animation on Our Purpose Section */}
        <section className="w-full bg-[var(--box-bg-color)] py-12 mb-8 rounded-lg shadow-lg border-2 border-[var(--border-color)]"> {/* Adjusted padding */}
          {/* Placeholder for actual animation/content */}
          <div className="h-72 md:h-96 bg-[var(--main-bg-color)] rounded-lg mt-0 mx-4 md:mx-12 flex items-center justify-center text-[var(--primary-color)] text-lg italic"> {/* Adjusted margin and increased height */}
            <p>A delightful animation explaining WyshDrop's mission will go here!</p>
          </div>
        </section>

        {/* All other sections now need horizontal padding */}
        <div className="w-full mx-auto"> {/* Adjusted margins to be smaller */}
          {/* Trending Section */}
          <ProductSection title="TRENDING" onSeeMoreClick={onSeeMoreClick} onProductClick={onProductClick} onAddProductToWishlist={onAddProductToWishlist} soundSettings={soundSettings} clickSoundRef={clickSoundRef} />

          {/* New Sections */}
          <ProductSection title="Books" onSeeMoreClick={onSeeMoreClick} onProductClick={onProductClick} onAddProductToWishlist={onAddProductToWishlist} soundSettings={soundSettings} clickSoundRef={clickSoundRef} />
          <ProductSection title="Accessories" onSeeMoreClick={onSeeMoreClick} onProductClick={onProductClick} onAddProductToWishlist={onAddProductToWishlist} soundSettings={soundSettings} clickSoundRef={clickSoundRef} />
          <ProductSection title="DIY/Art" onSeeMoreClick={onSeeMoreClick} onProductClick={onProductClick} onAddProductToWishlist={onAddProductToWishlist} soundSettings={soundSettings} clickSoundRef={clickSoundRef} />
          <ProductSection title="Tech" onSeeMoreClick={onSeeMoreClick} onProductClick={onProductClick} onAddProductToWishlist={onAddProductToWishlist} soundSettings={soundSettings} clickSoundRef={clickSoundRef} />
          <ProductSection title="Cups/Drinks" onSeeMoreClick={onSeeMoreClick} onProductClick={onProductClick} onAddProductToWishlist={onAddProductToWishlist} soundSettings={soundSettings} clickSoundRef={clickSoundRef} />
          <ProductSection title="Stationary" onSeeMoreClick={onSeeMoreClick} onProductClick={onProductClick} onAddProductToWishlist={onAddProductToWishlist} soundSettings={soundSettings} clickSoundRef={clickSoundRef} />
          <ProductSection title="Music" onSeeMoreClick={onSeeMoreClick} onProductClick={onProductClick} onAddProductToWishlist={onAddProductToWishlist} soundSettings={soundSettings} clickSoundRef={clickSoundRef} />
