import React, { useRef, useState, useEffect, useCallback } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, GoogleAuthProvider, signInWithPopup } from 'firebase/auth'; // Import necessary Firebase Auth modules

// Import the functions you need from the SDKs you need
//import { getAnalytics } from "firebase/analytics";
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

// Your web app's Firebase configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyATZACrgNqKqIJM_KJbasCZMSm11n7JggM",
  authDomain: "wyshdrop.firebaseapp.com",
  projectId: "wyshdrop",
  storageBucket: "wyshdrop.firebasestorage.app",
  messagingSenderId: "203381869133",
  appId: "1:203381869133:web:29763cff7413d9e523aa04",
  measurementId: "G-TK0DE56N5C"
};

// Check if placeholder API key is still present and log a warning
if (firebaseConfig.apiKey === "YOUR_API_KEY") {
  console.error("Firebase Configuration Error: Please replace 'YOUR_API_KEY' and other placeholder values in firebaseConfig with your actual Firebase project credentials from the Firebase Console.");
}


// Initialize Firebase client-side app
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);

// Reusable ProductSection Component (used on Home page and Category pages)
const ProductSection = ({ title, onSeeMoreClick, onProductClick }) => {
  const scrollRef = useRef(null);
  const scrollAmount = 300; // Pixels to scroll

  const handleScrollRight = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({ left: scrollAmount, behavior: 'smooth' });
    }
  };

  // Dummy product data for this section
  const dummyProducts = [...Array(5)].map((_, i) => ({
    id: `${title.replace(/\s/g, '-')}-product-${i + 1}`,
    name: `${title} Item ${i + 1}`,
    image: `https://placehold.co/240x300/D3A173/FFFFFF?text=${title.replace(/\s/g, '+')}+Item+${i + 1}`, // Updated placeholder size for 4:5 ratio
    price: (20 + i * 5).toFixed(2),
    description: `A wonderful product from the ${title} collection. Perfect for gifting or keeping!`,
    rating: Math.floor(Math.random() * 2) + 4, // 4 or 5 stars
    comments: [
      { id: 1, user: "UserA", text: "Love this product! Highly recommend." },
      { id: 2, user: "UserB", text: "Great quality for the price." },
    ]
  }));

  // THIS IS THE CORRECT PLACE FOR THE COMPONENT'S MAIN RETURN STATEMENT
  return (
    <section className="mb-12 relative"> {/* Increased margin-bottom for more space between sections */}
      <div className="flex justify-between items-center mb-4 ml-4 md:ml-12"> {/* Adjusted margin */}
        {/* Formal font for section titles */}
        <h2 className="text-3xl font-bold text-[var(--primary-color)]">{title}</h2>
        {/* See More link */}
        <a href="#" onClick={(e) => { e.preventDefault(); onSeeMoreClick(title); }} className="text-sm text-[var(--primary-color)] hover:underline mr-4 md:mr-12 flex items-center space-x-1 transition-all duration-200 ease-in-out hover:scale-105 active:scale-95"> {/* Added animation */}
          <span>See More</span>
        </a>
      </div>
      {/* Increased padding-bottom to pb-16 to ensure "Add to Wyshlist" button is fully visible */}
      <div ref={scrollRef} className="flex overflow-x-auto overflow-y-hidden pb-16 space-x-4 custom-scrollbar px-4 md:px-12">
        {/* Product Image Cards */}
        <div className="flex space-x-4"> {/* Added a wrapper div for consistent spacing */}
          {dummyProducts.map((product) => (
            <div key={product.id} className="flex-shrink-0 w-56 h-80 bg-[var(--box-bg-color)] rounded-lg shadow-md border-2 border-[var(--border-color)] flex flex-col items-center justify-between p-2 cursor-pointer transition-all duration-200 ease-in-out hover:scale-105 active:scale-95"
              onClick={() => onProductClick(product)}> {/* Adjusted padding and card size */}
